import React, { useState, useEffect, useRef, useCallback } from 'react';

// ==================== ICON IMPORTS ====================
import { 
  Brain, Zap, Heart, Sparkles, AlertCircle, Target, 
  Moon, Sun, Coffee, Activity, Shield, Lock, 
  TrendingUp, Clock, CheckCircle, XCircle,
  MessageCircle, Settings, Info, Award, Database,
  Mic, MicOff, Volume2, VolumeX, Save, Download,
  FileText, BookOpen, Star, Gift, HelpCircle,
  AlertTriangle, ChevronRight, RefreshCw, User,
  Send, Bell, Bookmark, ChevronDown, ChevronUp,
  Circle, Plus, Minus, Menu, X, Search,
  Phone
} from 'lucide-react';

// ==================== CONFIGURATION ====================
const TONE_MODES = {
  supportive: { 
    icon: 'ðŸ’™', 
    label: 'Supportive', 
    desc: 'Warm, empathetic, lots of emotional validation',
    color: 'bg-rose-100 border-rose-300 text-rose-800'
  },
  sassy: { 
    icon: 'ðŸ’…', 
    label: 'Sassy', 
    desc: 'Confident, playful, like your best friend',
    color: 'bg-pink-100 border-pink-300 text-pink-800'
  },
  direct: { 
    icon: 'ðŸ“Š', 
    label: 'Direct', 
    desc: 'Facts-focused, evidence-based responses',
    color: 'bg-purple-100 border-purple-300 text-purple-800'
  },
  clinical: { 
    icon: 'ðŸ¥', 
    label: 'Clinical', 
    desc: 'Medical terminology and clinical perspective',
    color: 'bg-teal-100 border-teal-300 text-teal-800'
  },
  friendly: { 
    icon: 'ðŸ˜Š', 
    label: 'Friendly', 
    desc: 'Casual, conversational, like chatting with a friend',
    color: 'bg-amber-100 border-amber-300 text-amber-800'
  }
};

const EMERGENCY_LEVELS = {
  critical: { color: 'red', icon: 'ðŸš¨', label: 'CRITICAL', bg: 'bg-red-500/20', border: 'border-red-500' },
  urgent: { color: 'orange', icon: 'âš ï¸', label: 'URGENT', bg: 'bg-orange-500/20', border: 'border-orange-500' },
  concerning: { color: 'yellow', icon: 'âš ï¸', label: 'CONCERNING', bg: 'bg-yellow-500/20', border: 'border-yellow-500' }
};

const PREGNANCY_RESOURCES = [
  { title: "Nutrition Guide", icon: <BookOpen size={16} />, category: "health" },
  { title: "Exercise Tips", icon: <Activity size={16} />, category: "health" },
  { title: "Hospital Bag Checklist", icon: <Save size={16} />, category: "preparation" },
  { title: "Birth Plan Template", icon: <FileText size={16} />, category: "preparation" },
  { title: "Baby Names", icon: <User size={16} />, category: "fun" },
  { title: "Weekly Milestones", icon: <TrendingUp size={16} />, category: "development" },
  { title: "Common Concerns", icon: <HelpCircle size={16} />, category: "health" },
  { title: "Postpartum Care", icon: <Heart size={16} />, category: "preparation" }
];

// ==================== UTILITY COMPONENTS ====================
const LoadingDots = () => (
  <div className="flex space-x-1">
    <div className="w-2 h-2 bg-pink-400 rounded-full animate-bounce"></div>
    <div className="w-2 h-2 bg-pink-400 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
    <div className="w-2 h-2 bg-pink-400 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
  </div>
);

const SystemStatus = ({ status }) => {
  const getStatusColor = () => {
    if (status.performance > 90) return 'bg-green-400';
    if (status.performance > 70) return 'bg-yellow-400';
    return 'bg-red-400';
  };

  return (
    <div className="flex items-center gap-2">
      <div className={`w-3 h-3 ${getStatusColor()} rounded-full animate-pulse`}></div>
      <span className="text-xs text-pink-600">
        {status.successful_interactions} chats â€¢ {status.performance}% health
      </span>
    </div>
  );
};

const FloralDivider = () => (
  <div className="relative py-2">
    <div className="absolute inset-0 flex items-center">
      <div className="w-full border-t border-pink-100"></div>
    </div>
    <div className="relative flex justify-center">
      <span className="text-pink-400 px-2 bg-white text-sm">ðŸŒ¸</span>
    </div>
  </div>
);

const PregnancyProgressBar = ({ week }) => {
  const progress = Math.min(100, Math.max(0, (week / 40) * 100));
  
  return (
    <div className="mb-4">
      <div className="flex justify-between text-xs text-pink-700 mb-1">
        <span>Week {week || 0}</span>
        <span>{progress.toFixed(0)}%</span>
      </div>
      <div className="h-2 bg-pink-100 rounded-full overflow-hidden">
        <div 
          className="h-full bg-gradient-to-r from-pink-400 to-rose-500 rounded-full transition-all duration-500"
          style={{ width: `${progress}%` }}
        ></div>
      </div>
      <div className="flex justify-between text-xs text-pink-500 mt-1">
        <span>1st Tri</span>
        <span>2nd Tri</span>
        <span>3rd Tri</span>
      </div>
    </div>
  );
};

// ==================== MAIN FRIDAY AI COMPONENT ====================
const FridayAI = () => {
  // ========== STATE MANAGEMENT ==========
  // User & Session State
  const [userName, setUserName] = useState('');
  const [pregnancyWeek, setPregnancyWeek] = useState(0);
  const [isSetupComplete, setIsSetupComplete] = useState(false);

  // Conversation State
  const [currentMessage, setCurrentMessage] = useState('');
  const [conversationHistory, setConversationHistory] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentTone, setCurrentTone] = useState('supportive');
  const [sessionTopics, setSessionTopics] = useState([]);
  
  // Feature States
  const [voiceEnabled, setVoiceEnabled] = useState(false);
  const [encryptionEnabled, setEncryptionEnabled] = useState(true);
  const [showSystemPanel, setShowSystemPanel] = useState(false);
  const [showGoalPanel, setShowGoalPanel] = useState(false);
  const [showMemoryPanel, setShowMemoryPanel] = useState(false);
  const [showResourcePanel, setShowResourcePanel] = useState(false);
  
  // Advanced Features State - Legendary Memory
  const [legendaryMemory, setLegendaryMemory] = useState({
    conversations: [],
    topicMemory: {},
    emotionalPatterns: {},
    userPatterns: {},
    sessionInsights: {}
  });

  // Goal Coaching System
  const [activeGoals, setActiveGoals] = useState([]);
  const [completedGoals, setCompletedGoals] = useState([]);
  const [pendingCheckIns, setPendingCheckIns] = useState({});
  
  // System Health & Metrics
  const [systemHealth, setSystemHealth] = useState({
    uptime: Date.now(),
    performance: 100,
    successful_interactions: 0,
    error_count: 0,
    emotional_intelligence_level: 95,
    response_times: [],
    emergency_responses: 0,
    vault_operations: 0,
    predictions_made: 0
  });

  // Unstoppable Features
  const [unstoppableFeatures, setUnstoppableFeatures] = useState({
    resilience_active: true,
    predictive_analytics: true,
    emergency_protocol: true,
    secure_vault: true
  });

  // Self-Evaluation System
  const [selfEvaluation, setSelfEvaluation] = useState({
    interaction_count: 0,
    feedback_requests: 0,
    user_feedback_history: [],
    tone_adjustments: {},
    last_feedback_request: null
  });

  // Citation System
  const [citationUsage, setCitationUsage] = useState({});

  // Knowledge Injection
  const [knowledgeStats, setKnowledgeStats] = useState({
    facts_shared: 0,
    categories: {}
  });

  // UI State
  const messagesEndRef = useRef(null);
  const [showEmergencyChecklist, setShowEmergencyChecklist] = useState(false);
  const [showPerformanceReport, setShowPerformanceReport] = useState(false);
  const [showSetup, setShowSetup] = useState(true);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // ========== EFFECTS ==========
  useEffect(() => {
    // Scroll to bottom of messages
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [conversationHistory]);

  useEffect(() => {
    // Predictive assistance based on pregnancy week
    if (pregnancyWeek > 0 && conversationHistory.length === 0) {
      setTimeout(() => {
        if (pregnancyWeek < 12) {
          addMessage('ai', `ðŸŒ¸ Welcome to your pregnancy journey! At ${pregnancyWeek} weeks, you might be experiencing early symptoms like nausea or fatigue. Remember to stay hydrated and rest when needed. How are you feeling today?`, {
            tone: 'supportive',
            predictive: true
          });
        } else if (pregnancyWeek > 28) {
          addMessage('ai', `ðŸ‘‹ Hello! As you enter your third trimester at ${pregnancyWeek} weeks, you might be thinking about birth preparation. Would you like help creating a birth plan or hospital bag checklist?`, {
            tone: 'friendly',
            predictive: true
          });
        }
      }, 1500);
    }
  }, [pregnancyWeek, conversationHistory.length]);

  // ========== CORE ANALYSIS FUNCTIONS ==========
  const analyzeInputSemantic = useCallback((input) => {
    const text = input.toLowerCase();
    
    // Emergency Detection First
    const emergencyKeywords = {
      bleeding: /\b(bleeding|blood|spotting heavily|gushing)\b/gi,
      pain: /\b(severe pain|intense pain|unbearable|sharp pain)\b/gi,
      breathing: /\b(can't breathe|trouble breathing|shortness of breath)\b/gi,
      contractions: /\b(contractions|labor pains|regular contractions)\b/gi,
      water_broke: /\b(water broke|water breaking|fluid leaking)\b/gi,
      baby_movement: /\b(baby not moving|no movement|baby stopped moving)\b/gi,
      consciousness: /\b(dizzy|faint|passing out|unconscious)\b/gi,
      fall_injury: /\b(fell down|car accident|hit my belly|trauma)\b/gi
    };

    // Check for emergencies
    for (const [type, pattern] of Object.entries(emergencyKeywords)) {
      if (pattern.test(text)) {
        // Determine urgency level
        let urgencyLevel = 'urgent';
        if (['bleeding', 'consciousness', 'breathing'].includes(type)) {
          urgencyLevel = 'critical';
        } else if (['baby_movement', 'fall_injury'].includes(type)) {
          urgencyLevel = 'concerning';
        }
        
        return {
          type: 'emergency',
          emergency_type: type,
          urgency_level: urgencyLevel,
          confidence: 1.0
        };
      }
    }

    // Emotional Analysis
    const emotions = {
      anxiety: /\b(worried|anxious|scared|nervous|panic|stress|overwhelming)\b/g,
      joy: /\b(happy|excited|joy|thrilled|amazing|wonderful|great)\b/g,
      sadness: /\b(sad|depressed|down|crying|upset|hurt|disappointed)\b/g,
      fatigue: /\b(tired|exhausted|drained|sleepy|worn out|fatigued)\b/g,
      pain: /\b(pain|hurt|ache|sore|cramp|discomfort)\b/g
    };

    const detectedEmotions = [];
    Object.entries(emotions).forEach(([emotion, pattern]) => {
      if (pattern.test(text)) {
        detectedEmotions.push(emotion);
      }
    });

    // Pregnancy Topic Detection
    const pregnancyTopics = {
      physical: /\b(nausea|vomiting|morning sickness|back pain|swelling|headache)\b/g,
      emotional: /\b(mood swings|hormonal|emotional|crying|irritable)\b/g,
      developmental: /\b(baby|fetus|growth|development|movement|kicks|heartbeat)\b/g,
      medical: /\b(doctor|appointment|test|ultrasound|blood work|checkup)\b/g
    };

    const detectedTopics = [];
    Object.entries(pregnancyTopics).forEach(([topic, pattern]) => {
      if (pattern.test(text)) {
        detectedTopics.push(topic);
      }
    });

    const isQuestion = /\?|what|how|when|where|why|should|can|will|is it/i.test(text);

    return {
      type: detectedTopics.length > 0 ? 'pregnancy_related' : 'general_conversation',
      emotions: detectedEmotions,
      pregnancy_topics: detectedTopics,
      is_question: isQuestion,
      confidence: 0.85 + (detectedEmotions.length * 0.05)
    };
  }, []);

  // ========== RESPONSE GENERATION ==========
  const generateResponse = useCallback((userInput, analysis) => {
    const baseResponses = {
      emergency: {
        bleeding: "ðŸš¨ **CRITICAL EMERGENCY - IMMEDIATE ACTION NEEDED** ðŸš¨\n\n**CALL 911 (or your emergency number) RIGHT NOW**\n\nWhile waiting for help:\n1. Lie down and elevate your feet\n2. Do not insert anything into the vagina\n3. Keep track of the amount of bleeding\n4. Stay calm - help is coming\n\n**Do not wait. This requires immediate medical attention.**",
        pain: "âš ï¸ **URGENT - MEDICAL ATTENTION NEEDED** âš ï¸\n\n**Contact your healthcare provider immediately or go to the hospital**\n\nThis situation needs prompt medical evaluation.",
        baby_movement: "âš ï¸ **CONCERNING SYMPTOMS - SEEK MEDICAL ADVICE** âš ï¸\n\n**Immediate steps:**\n1. Lie on your left side\n2. Drink something cold and sweet\n3. Count movements for 10 minutes\n4. If still no movement: **Go to hospital immediately**",
        default: "ðŸš¨ **MEDICAL EMERGENCY** ðŸš¨\n\n**Seek immediate medical attention**\n\nDon't wait - your safety and your baby's safety are the priority."
      },
      supportive: {
        anxiety: `I understand you're feeling anxious, ${userName || 'dear'}. These feelings are completely valid and normal during pregnancy. Your body is going through so many changes, and it's natural to feel worried. Let's work through this together. What specific worries are on your mind right now?`,
        joy: `Your excitement is absolutely beautiful, ${userName || 'friend'}! There's something magical about the joy that comes with pregnancy. This positive energy is wonderful for both you and your baby. Tell me what's bringing you such happiness today!`,
        general: `I'm here for you, ${userName || 'friend'}. Whatever you're experiencing right now, your feelings matter and they're valid. How can I support you best in this moment?`
      },
      sassy: {
        anxiety: `Girl, I see those worries trying to take over! Listen, ${userName || 'honey'}, you're stronger than any anxiety. Every mama has these moments, but you? You're gonna rock this! Now spill - what's got you stressed?`,
        joy: `YES QUEEN! ${userName || 'babe'}, that excitement is EVERYTHING! You're literally glowing through this screen! Tell me all about what's got you feeling so amazing!`,
        general: `Hey there ${userName || 'gorgeous'}! Mama Friday is here and ready to chat. Whatever's on your mind, we're gonna tackle it together. What's the tea today?`
      },
      direct: {
        general: `Based on current medical evidence: I'm ready to provide factual information. Please specify your primary concern for targeted, evidence-based guidance.`,
        pregnancy: `Current pregnancy week: ${pregnancyWeek || 'unspecified'}. Provide specific symptoms or questions for evidence-based medical information.`
      },
      clinical: {
        general: `Clinical assessment initiated. Patient presenting with query regarding pregnancy-related concerns. Awaiting specific symptomatology for appropriate medical guidance.`,
        anxiety: `Maternal anxiety noted. Elevated stress hormones during gestation can impact fetal development. Recommend consultation with obstetric care provider for anxiety management strategies.`
      },
      friendly: {
        general: `Hey there! I'm so glad you reached out today. Whatever's going on, we can chat about it together. I'm all ears! What's on your mind?`,
        anxiety: `Oh sweetie, I can tell something's bothering you. That's totally okay - pregnancy can bring up so many feelings! Want to talk about what's making you anxious?`
      }
    };

    // Emotional validation phrases
    const emotionalValidation = {
      anxiety: `It's completely normal to feel this way during pregnancy. Your body is going through incredible changes!`,
      pain: `I'm sorry you're experiencing discomfort. Remember you're stronger than you feel right now ðŸ’ª`,
      joy: `This makes me so happy to hear! Your joy is contagious ðŸŒ¸`,
      sadness: `I'm here with you through these difficult moments. You're not alone.`,
      fatigue: `Your body is working so hard right now. Rest is important and completely valid.`,
      default: `Thank you for sharing this with me.`
    };

    // Handle emergencies
    if (analysis.type === 'emergency') {
      return {
        content: baseResponses.emergency[analysis.emergency_type] || baseResponses.emergency.default,
        isEmergency: true,
        urgencyLevel: analysis.urgency_level,
        confidence: 1.0
      };
    }

    // Select appropriate response based on tone and emotion
    const toneResponses = baseResponses[currentTone] || baseResponses.supportive;
    let selectedResponse;

    if (analysis.emotions.includes('anxiety')) {
      selectedResponse = toneResponses.anxiety || toneResponses.general;
    } else if (analysis.emotions.includes('joy')) {
      selectedResponse = toneResponses.joy || toneResponses.general;
    } else {
      selectedResponse = toneResponses.general;
    }

    // Add emotional validation
    const primaryEmotion = analysis.emotions[0] || 'default';
    selectedResponse = `${emotionalValidation[primaryEmotion]} ${selectedResponse}`;

    // Add pregnancy week context
    if (pregnancyWeek > 0 && analysis.pregnancy_topics.length > 0) {
      selectedResponse += `\n\nAt ${pregnancyWeek} weeks, you're ${getPregnancyStage(pregnancyWeek)}. `;
      
      if (pregnancyWeek < 13) {
        selectedResponse += "The first trimester can be challenging with all the changes your body is experiencing.";
      } else if (pregnancyWeek < 27) {
        selectedResponse += "The second trimester often brings more energy and comfort - enjoy this special time!";
      } else {
        selectedResponse += "You're in the home stretch! The third trimester is when your baby does most of their growing.";
      }
    }

    return {
      content: selectedResponse,
      isEmergency: false,
      confidence: analysis.confidence
    };
  }, [userName, pregnancyWeek, currentTone]);

  // ========== HELPER FUNCTIONS ==========
  const getPregnancyStage = (week) => {
    if (week < 13) return "in your first trimester";
    if (week < 27) return "in your second trimester";
    return "in your third trimester";
  };

  const detectNameInMessage = (message) => {
    const patterns = [
      /(?:call me|my name is|i'm|i am|this is)\s+([A-Za-z]+)/i,
      /([A-Za-z]+)\s+here/i
    ];

    for (const pattern of patterns) {
      const match = message.match(pattern);
      if (match && match[1]) {
        return match[1].charAt(0).toUpperCase() + match[1].slice(1).toLowerCase();
      }
    }
    return null;
  };

  const formatTimestamp = (date) => {
    return new Intl.DateTimeFormat('en-US', {
      hour: 'numeric',
      minute: '2-digit',
      hour12: true
    }).format(date);
  };

  const findSimilarConversation = useCallback((userInput) => {
    const extractKeywords = (text) => {
      const stopWords = ['i', 'am', 'is', 'are', 'the', 'a', 'an', 'and', 'or', 'but'];
      return text.toLowerCase().split(/\s+/).filter(word => 
        word.length > 2 && !stopWords.includes(word)
      );
    };

    const currentKeywords = extractKeywords(userInput);
    let bestMatch = null;
    let bestScore = 0;

    legendaryMemory.conversations.forEach(conv => {
      const pastKeywords = extractKeywords(conv.userInput);
      const intersection = currentKeywords.filter(word => pastKeywords.includes(word));
      const score = intersection.length / Math.max(currentKeywords.length, pastKeywords.length);
      
      if (score > 0.5 && score > bestScore) {
        bestMatch = conv;
        bestScore = score;
      }
    });

    return bestMatch;
  }, [legendaryMemory.conversations]);

  const detectGoalOpportunity = useCallback((userInput, aiResponse, emotionalTone) => {
    const goalTriggers = {
      anxiety_management: ['anxious', 'worried', 'scared', 'stress', 'overwhelmed', 'panic'],
      birth_preparation: ['birth', 'delivery', 'labor', 'due date', 'hospital', 'contractions'],
      nutrition_health: ['eating', 'nutrition', 'weight', 'vitamins', 'healthy', 'food'],
      postpartum_preparation: ['after birth', 'postpartum', 'recovery', 'newborn', 'baby care']
    };

    const userLower = userInput.toLowerCase();
    const hasAdvice = ['try', 'consider', 'help', 'suggest', 'recommend'].some(word => 
      aiResponse.toLowerCase().includes(word)
    );

    if (hasAdvice || ['anxious', 'scared', 'overwhelmed'].includes(emotionalTone)) {
      for (const [goalType, triggers] of Object.entries(goalTriggers)) {
        if (triggers.some(trigger => userLower.includes(trigger))) {
          const hasGoal = activeGoals.some(g => g.type === goalType);
          if (!hasGoal) return goalType;
        }
      }
    }
    return null;
  }, [activeGoals]);

  const shouldRequestFeedback = useCallback(() => {
    const count = selfEvaluation.interaction_count;
    
    if (selfEvaluation.last_feedback_request) {
      const timeSince = Date.now() - selfEvaluation.last_feedback_request.getTime();
      if (timeSince < 600000) return false;
    }
    
    return count === 7 || count === 20 || count === 50 || 
           (count > 50 && count % 25 === 0) ||
           (count > 10 && Math.random() < 0.06);
  }, [selfEvaluation]);

  const getPredictedMilestones = useCallback((weeks) => {
    const milestones = {
      6: ["First prenatal appointment typically scheduled", "Morning sickness may begin"],
      8: ["Morning sickness might peak around week 9-10", "First prenatal blood work"],
      12: ["End of first trimester!", "Risk of miscarriage significantly decreases"],
      16: ["You might feel first movements soon", "Anatomy scan appointment to schedule"],
      20: ["Anatomy scan - major milestone!", "Halfway through your pregnancy!"],
      24: ["Glucose screening test typically scheduled", "Baby reaches viability milestone"],
      28: ["Third trimester begins", "More frequent prenatal visits begin"],
      36: ["Baby is considered full-term at 37 weeks", "Final preparations time"],
      40: ["Due date week", "Baby could arrive any time now"]
    };

    const upcoming = [];
    for (let w = weeks; w <= Math.min(weeks + 3, 40); w++) {
      if (milestones[w]) {
        milestones[w].forEach(m => {
          if (w === weeks) upcoming.push(`This week: ${m}`);
          else if (w === weeks + 1) upcoming.push(`Next week: ${m}`);
          else upcoming.push(`Week ${w}: ${m}`);
        });
      }
    }
    return upcoming;
  }, []);

  // ========== MESSAGE HANDLING ==========
  const handleSendMessage = useCallback(() => {
    if (!currentMessage.trim() || isLoading) return;

    const messageText = currentMessage.trim();
    setCurrentMessage('');
    setIsLoading(true);

    const startTime = Date.now();

    // Check for special commands
    if (messageText.startsWith('!')) {
      handleSpecialCommand(messageText);
      setIsLoading(false);
      return;
    }

    // Check for name change
    const detectedName = detectNameInMessage(messageText);
    if (detectedName && detectedName !== userName) {
      setUserName(detectedName);
      addMessage('user', messageText);
      addMessage('ai', `Perfect! I'll call you ${detectedName} from now on. It's wonderful to know your name! ðŸ’™\n\nHow can I help you today, ${detectedName}?`, {
        tone: currentTone,
        special: 'name_change'
      });
      setIsLoading(false);
      return;
    }

    // Check for goal acceptance
    if (['yes', 'create goal', 'set goal', "i'm in", 'help me'].includes(messageText.toLowerCase())) {
      const lastMessage = conversationHistory[conversationHistory.length - 1];
      if (lastMessage?.goal_offer) {
        handleGoalCreation();
        setIsLoading(false);
        return;
      }
    }

    // Add user message
    addMessage('user', messageText);

    // Simulate AI processing
    setTimeout(() => {
      const analysis = analyzeInputSemantic(messageText);
      const response = generateResponse(messageText, analysis);
      
      // Find similar past conversation
      const similarConv = findSimilarConversation(messageText);
      let finalContent = response.content;
      
      if (similarConv && !response.isEmergency) {
        const daysAgo = Math.floor((Date.now() - similarConv.timestamp) / (1000 * 60 * 60 * 24));
        const timeRef = daysAgo > 0 ? `${daysAgo} days ago` : "earlier today";
        finalContent = `ðŸ’­ **I remember** we talked about something similar ${timeRef}. Let me build on what we discussed.\n\n${finalContent}`;
      }

      // Add milestones if relevant
      if (pregnancyWeek > 0 && (messageText.includes('milestone') || Math.random() < 0.15)) {
        const milestones = getPredictedMilestones(pregnancyWeek);
        if (milestones.length > 0) {
          finalContent += `\n\nðŸ“… **Upcoming Milestones:**\n${milestones.slice(0, 2).map(m => `â€¢ ${m}`).join('\n')}`;
        }
      }

      // Check for goal opportunity
      const goalOpp = detectGoalOpportunity(messageText, response.content, analysis.emotions[0] || 'neutral');
      if (goalOpp && !similarConv) {
        finalContent += createGoalOffer(goalOpp);
      }

      // Check for feedback request
      if (shouldRequestFeedback()) {
        finalContent += `\n\nðŸ’­ Quick check-in: How am I doing with my ${currentTone} tone? Should I adjust anything?`;
        setSelfEvaluation(prev => ({
          ...prev,
          last_feedback_request: new Date()
        }));
      }

      // Update metrics
      const endTime = Date.now();
      const responseTime = endTime - startTime;
      
      setSystemHealth(prev => ({
        ...prev,
        successful_interactions: prev.successful_interactions + 1,
        emergency_responses: response.isEmergency ? prev.emergency_responses + 1 : prev.emergency_responses,
        response_times: [...prev.response_times.slice(-99), responseTime].slice(-100)
      }));

      setSelfEvaluation(prev => ({
        ...prev,
        interaction_count: prev.interaction_count + 1
      }));

      // Update legendary memory
      updateLegendaryMemory(messageText, finalContent, analysis);

      // Add AI response
      addMessage('ai', finalContent, {
        tone: currentTone,
        emotional_tone: analysis.emotions[0] || 'neutral',
        analysis,
        isEmergency: response.isEmergency,
        emergencyLevel: response.urgencyLevel,
        confidence: response.confidence,
        memory_recall: !!similarConv,
        goal_offer: !!goalOpp
      });

      setIsLoading(false);
    }, 800 + Math.random() * 400);
  }, [currentMessage, isLoading, userName, currentTone, conversationHistory, pregnancyWeek, analyzeInputSemantic, generateResponse, findSimilarConversation, detectGoalOpportunity, shouldRequestFeedback, getPredictedMilestones]);

  const addMessage = (type, content, metadata = {}) => {
    const message = {
      id: `${Date.now()}-${Math.random()}`,
      type,
      content,
      timestamp: new Date(),
      ...metadata
    };
    setConversationHistory(prev => [...prev, message]);
  };

  const updateLegendaryMemory = (userInput, aiResponse, analysis) => {
    setLegendaryMemory(prev => {
      const topic = analysis.pregnancy_topics[0] || 'general';
      const emotion = analysis.emotions[0] || 'neutral';

      return {
        ...prev,
        conversations: [...prev.conversations, { 
          userInput, 
          aiResponse, 
          timestamp: Date.now(),
          emotionalTone: emotion 
        }].slice(-100),
        topicMemory: {
          ...prev.topicMemory,
          [topic]: [...(prev.topicMemory[topic] || []), { userInput, timestamp: Date.now() }].slice(-10)
        },
        emotionalPatterns: {
          ...prev.emotionalPatterns,
          [emotion]: (prev.emotionalPatterns[emotion] || 0) + 1
        }
      };
    });

    if (analysis.pregnancy_topics.length > 0) {
      setSessionTopics(prev => [...prev, ...analysis.pregnancy_topics].slice(-20));
    }
  };

  // ========== SPECIAL COMMANDS ==========
  const handleSpecialCommand = useCallback((command) => {
    const cmd = command.toLowerCase().trim();

    if (cmd === '!status') {
      showSystemStatus();
    } else if (cmd === '!goals') {
      showGoalsStatus();
    } else if (cmd === '!emergency' || cmd === '!emergency_checklist') {
      setShowEmergencyChecklist(true);
    } else if (cmd.startsWith('!tone')) {
      handleToneCommand(cmd);
    } else if (cmd === '!memory') {
      showMemoryStatus();
    } else if (cmd === '!performance') {
      setShowPerformanceReport(true);
    } else if (cmd.startsWith('!feedback')) {
      handleFeedbackCommand(cmd);
    } else {
      addMessage('ai', "â“ Available commands: !status, !goals, !emergency, !tone, !memory, !performance, !feedback", {
        tone: 'system',
        special: 'help'
      });
    }
  }, []);

  const showSystemStatus = () => {
    const uptime = Date.now() - systemHealth.uptime;
    const days = Math.floor(uptime / (1000 * 60 * 60 * 24));
    const hours = Math.floor((uptime % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    const avgResponseTime = systemHealth.response_times.length > 0 
      ? (systemHealth.response_times.reduce((a, b) => a + b, 0) / systemHealth.response_times.length / 1000).toFixed(2)
      : 'N/A';

    const statusMessage = `ðŸ† **Friday AI System Status**

**Core Systems:**
â€¢ ðŸ§  Legendary Memory: âœ… Active (${legendaryMemory.conversations.length} conversations)
â€¢ ðŸŽ¯ Goal Coaching: âœ… Active (${activeGoals.length} active goals)
â€¢ ðŸš¨ Emergency Detection: âœ… Active (${systemHealth.emergency_responses} handled)
â€¢ ðŸ” Secure Vault: ${encryptionEnabled ? 'âœ… Encrypted' : 'âš ï¸ Basic Mode'}
â€¢ ðŸŽ¤ Voice Interface: ${voiceEnabled ? 'âœ… Enabled' : 'âŒ Disabled'}

**Performance Metrics:**
â€¢ â±ï¸ Uptime: ${days}d ${hours}h
â€¢ ðŸ“Š Success Rate: ${systemHealth.performance}%
â€¢ ðŸ’¬ Total Interactions: ${systemHealth.successful_interactions}
â€¢ âš¡ Avg Response Time: ${avgResponseTime}s
â€¢ âŒ Errors: ${systemHealth.error_count}

**Unstoppable Features:**
â€¢ ðŸ›¡ï¸ Resilience: ${unstoppableFeatures.resilience_active ? 'âœ…' : 'âŒ'}
â€¢ ðŸ“ˆ Predictive Analytics: ${unstoppableFeatures.predictive_analytics ? 'âœ…' : 'âŒ'}
â€¢ ðŸš¨ Emergency Protocol: ${unstoppableFeatures.emergency_protocol ? 'âœ…' : 'âŒ'}
â€¢ ðŸ”’ Secure Vault: ${unstoppableFeatures.secure_vault ? 'âœ…' : 'âŒ'}

**Current Session:**
â€¢ ðŸ‘¤ User: ${userName || 'Guest'}
â€¢ ðŸ¤° Pregnancy Week: ${pregnancyWeek || 'Not specified'}
â€¢ ðŸŽ­ Tone: ${currentTone}
â€¢ ðŸ“ Topics: ${sessionTopics.slice(-3).join(', ') || 'None yet'}`;

    addMessage('ai', statusMessage, {
      tone: 'system',
      special: 'status'
    });
  };

  // ========== UI COMPONENTS ==========
  const MessageBubble = ({ message }) => {
    const isUser = message.type === 'user';
    const toneConfig = TONE_MODES[message.tone || 'supportive'];
    
    return (
      <div className={`flex ${isUser ? 'justify-end' : 'justify-start'}`}>
        <div 
          className={`
            p-4 rounded-2xl max-w-[85%] relative
            ${isUser 
              ? 'bg-gradient-to-r from-pink-500 to-rose-500 text-white rounded-br-none' 
              : `${toneConfig.color} rounded-bl-none`}
          `}
          style={{ 
            boxShadow: '0 4px 10px rgba(0,0,0,0.05)',
            borderWidth: '1px',
            borderStyle: 'solid',
            borderColor: isUser ? 'transparent' : toneConfig.color.split(' ')[1].replace('border-', '')
          }}
        >
          {message.isEmergency && (
            <div className={`flex items-center mb-2 p-2 rounded-lg ${EMERGENCY_LEVELS[message.emergencyLevel].bg} ${EMERGENCY_LEVELS[message.emergencyLevel].border} border`}>
              <span className="mr-2">{EMERGENCY_LEVELS[message.emergencyLevel].icon}</span>
              <span className="font-bold">{EMERGENCY_LEVELS[message.emergencyLevel].label}</span>
            </div>
          )}
          
          <div className="prose prose-pink">
            {message.content.split('\n\n').map((paragraph, i) => (
              <p key={i} className="mb-2 last:mb-0">{paragraph}</p>
            ))}
          </div>
          
          <div className={`absolute w-4 h-4 ${isUser ? '-right-4 top-0' : '-left-4 top-0'}`}>
            {isUser ? (
              <div className="w-full h-full bg-gradient-to-r from-pink-500 to-rose-500 rounded-br-full"></div>
            ) : (
              <div className={`w-full h-full ${toneConfig.color.split(' ')[0]} rounded-bl-full`}></div>
            )}
          </div>
          
          <div className="text-xs opacity-70 mt-1 text-right">
            {formatTimestamp(message.timestamp)}
          </div>
        </div>
      </div>
    );
  };

  const ToneSelector = () => (
    <div className="flex flex-wrap gap-2 justify-center mt-4">
      {Object.entries(TONE_MODES).map(([key, config]) => (
        <button
          key={key}
          className={`flex items-center px-3 py-2 rounded-full border transition-all ${
            currentTone === key 
              ? `${config.color} border-${config.color.split('border-')[1]} font-bold scale-105`
              : 'bg-white border-pink-200 hover:bg-pink-50'
          }`}
          onClick={() => setCurrentTone(key)}
        >
          <span className="mr-1">{config.icon}</span>
          {config.label}
        </button>
      ))}
    </div>
  );

  const InitialSetup = () => (
    <div className="bg-white p-6 rounded-2xl shadow-lg max-w-md mx-auto border border-pink-100">
      <div className="text-center mb-6">
        <div className="bg-gradient-to-r from-pink-300 to-rose-400 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
          <Sparkles className="text-white w-8 h-8" />
        </div>
        <h2 className="text-2xl font-bold text-pink-700">Welcome to Your Pregnancy Companion</h2>
        <p className="text-gray-600 mt-2">
          Let's personalize your experience for the best support
        </p>
      </div>
      
      <div className="space-y-4">
        <div>
          <label className="block text-pink-700 mb-1">Your Name</label>
          <input
            type="text"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            className="w-full p-3 border border-pink-200 rounded-xl focus:ring-2 focus:ring-pink-300 focus:outline-none"
            placeholder="What should I call you?"
          />
        </div>
        
        <div>
          <label className="block text-pink-700 mb-1">Current Pregnancy Week</label>
          <div className="flex items-center space-x-2">
            <button 
              className="bg-pink-100 text-pink-700 p-2 rounded-full"
              onClick={() => pregnancyWeek > 0 && setPregnancyWeek(pw => pw - 1)}
            >
              <Minus size={16} />
            </button>
            <div className="flex-1 text-center text-lg font-bold text-pink-700">
              Week {pregnancyWeek}
            </div>
            <button 
              className="bg-pink-100 text-pink-700 p-2 rounded-full"
              onClick={() => pregnancyWeek < 40 && setPregnancyWeek(pw => pw + 1)}
            >
              <Plus size={16} />
            </button>
          </div>
        </div>
        
        <div className="pt-4">
          <PregnancyProgressBar week={pregnancyWeek} />
        </div>
        
        <div className="mt-6">
          <button
            className="w-full bg-gradient-to-r from-pink-500 to-rose-500 text-white py-3 rounded-xl font-bold shadow-lg hover:shadow-xl transition-all"
            onClick={() => {
              setIsSetupComplete(true);
              setShowSetup(false);
            }}
          >
            Start Your Journey
          </button>
        </div>
        
        <div className="text-center text-xs text-pink-500 mt-4 flex items-center justify-center">
          <Lock className="mr-1" size={12} />
          <span>Your privacy is protected. All data is encrypted.</span>
        </div>
      </div>
    </div>
  );

  const EmergencyChecklist = () => (
    <div className="fixed inset-0 bg-black/70 z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl max-w-md w-full overflow-hidden">
        <div className="bg-red-500 p-4 text-white">
          <div className="flex items-center">
            <AlertCircle className="mr-2" />
            <h3 className="text-xl font-bold">Emergency Checklist</h3>
          </div>
        </div>
        
        <div className="p-4 max-h-[70vh] overflow-y-auto">
          <div className="mb-4">
            <h4 className="font-bold text-gray-700 mb-2">Immediate Actions:</h4>
            <ul className="list-disc pl-5 space-y-1 text-gray-700">
              <li>Call your healthcare provider immediately</li>
              <li>If severe, call emergency services (911)</li>
              <li>Note the time symptoms started</li>
              <li>Have someone stay with you</li>
              <li>Prepare your medical information</li>
            </ul>
          </div>
          
          <div className="mb-4">
            <h4 className="font-bold text-gray-700 mb-2">What to Bring to Hospital:</h4>
            <ul className="list-disc pl-5 space-y-1 text-gray-700">
              <li>ID and insurance information</li>
              <li>Prenatal records</li>
              <li>Phone and charger</li>
              <li>Comfortable clothing</li>
              <li>Toiletries</li>
              <li>Baby clothes for going home</li>
            </ul>
          </div>
          
          <div>
            <h4 className="font-bold text-gray-700 mb-2">Emergency Contacts:</h4>
            <div className="space-y-2">
              <div className="flex items-center p-2 bg-pink-50 rounded-lg">
                <div className="bg-red-100 p-2 rounded-full mr-3">
                  <Phone className="text-red-500" size={16} />
                </div>
                <div>
                  <div className="font-medium">Your Doctor</div>
                  <div className="text-sm text-gray-600">Dr. Smith: (555) 123-4567</div>
                </div>
              </div>
              <div className="flex items-center p-2 bg-pink-50 rounded-lg">
                <div className="bg-red-100 p-2 rounded-full mr-3">
                  <Phone className="text-red-500" size={16} />
                </div>
                <div>
                  <div className="font-medium">Emergency Contact</div>
                  <div className="text-sm text-gray-600">Partner: (555) 987-6543</div>
                </div>
              </div>
            </div>
          </div>
        </div>
        
        <div className="p-4 border-t border-gray-100 flex justify-between">
          <button 
            className="px-4 py-2 border border-gray-300 rounded-xl"
            onClick={() => setShowEmergencyChecklist(false)}
          >
            Close
          </button>
          <button className="px-4 py-2 bg-red-500 text-white rounded-xl font-medium">
            Call Emergency Services
          </button>
        </div>
      </div>
    </div>
  );

  const ResourcePanel = () => (
    <div className="absolute right-0 top-0 h-full w-full md:w-80 bg-white shadow-lg z-10">
      <div className="p-4 border-b border-pink-100 flex justify-between items-center">
        <h3 className="font-bold text-pink-700">Pregnancy Resources</h3>
        <button onClick={() => setShowResourcePanel(false)}>
          <X size={20} />
        </button>
      </div>
      
      <div className="p-4 space-y-4">
        {PREGNANCY_RESOURCES.map((resource, index) => (
          <div key={index} className="flex items-center p-3 bg-pink-50 rounded-xl border border-pink-100">
            <div className="bg-white p-2 rounded-lg mr-3 text-pink-500">
              {resource.icon}
            </div>
            <div className="font-medium text-pink-800">{resource.title}</div>
          </div>
        ))}
      </div>
    </div>
  );

  // ========== RENDER ==========
  return (
    <div className="relative bg-gradient-to-b from-rose-50 to-pink-50 min-h-screen flex flex-col">
      {/* Floral decoration */}
      <div className="absolute top-0 right-0 opacity-10">
        <svg width="200" height="200" viewBox="0 0 200 200" className="text-pink-300">
          <path d="M100,20 C120,10 140,15 160,30 C180,45 190,70 180,100 C170,130 140,150 100,180 C60,150 30,130 20,100 C10,70 20,45 40,30 C60,15 80,10 100,20 Z" fill="currentColor" />
        </svg>
      </div>
      
      {/* Header */}
      <header className="bg-gradient-to-r from-pink-500 to-rose-500 text-white p-4 rounded-b-2xl shadow-sm z-10">
        <div className="flex items-center justify-between">
          <div className="flex items-center">
            <div className="bg-white/20 p-1.5 rounded-lg mr-3">
              <Sparkles className="text-yellow-200" size={20} />
            </div>
            <h1 className="text-xl font-bold">Friday Pregnancy Companion</h1>
          </div>
          
          <div className="flex items-center space-x-3">
            <button className="p-1.5 bg-white/20 rounded-lg" onClick={() => setShowResourcePanel(true)}>
              <BookOpen size={20} />
            </button>
            <button className="p-1.5 bg-white/20 rounded-lg" onClick={() => setIsMenuOpen(!isMenuOpen)}>
              <Menu size={20} />
            </button>
          </div>
        </div>
        
        {isMenuOpen && (
          <div className="mt-4 bg-white/20 p-3 rounded-xl backdrop-blur-sm">
            <div className="grid grid-cols-2 gap-2">
              <button className="flex flex-col items-center p-2 bg-white/30 rounded-lg">
                <User size={18} />
                <span className="text-xs mt-1">Profile</span>
              </button>
              <button className="flex flex-col items-center p-2 bg-white/30 rounded-lg">
                <Settings size={18} />
                <span className="text-xs mt-1">Settings</span>
              </button>
              <button className="flex flex-col items-center p-2 bg-white/30 rounded-lg">
                <Heart size={18} />
                <span className="text-xs mt-1">Support</span>
              </button>
              <button className="flex flex-col items-center p-2 bg-white/30 rounded-lg">
                <Shield size={18} />
                <span className="text-xs mt-1">Privacy</span>
              </button>
            </div>
          </div>
        )}
      </header>
      
      {/* Setup Screen */}
      {showSetup && (
        <div className="flex-1 flex items-center justify-center p-4 z-10">
          <InitialSetup />
        </div>
      )}
      
      {/* Main Chat Interface */}
      {isSetupComplete && !showSetup && (
        <div className="flex-1 flex flex-col">
          {/* Tone Selector */}
          <ToneSelector />
          
          {/* Chat Messages */}
          <div className="flex-1 overflow-y-auto p-4 space-y-4 pb-24">
            {conversationHistory.map((message) => (
              <MessageBubble key={message.id} message={message} />
            ))}
            
            {isLoading && (
              <div className="flex justify-start">
                <div className="p-4 bg-pink-100 rounded-2xl rounded-bl-none max-w-[70%]">
                  <LoadingDots />
                </div>
              </div>
            )}
            
            <div ref={messagesEndRef} />
          </div>
          
          {/* Input Area */}
          <div className="fixed bottom-0 left-0 right-0 bg-white p-3 border-t border-pink-200 shadow-lg">
            <div className="flex items-center">
              <button className="bg-pink-100 p-2 rounded-full mr-2 text-pink-600">
                <Mic size={20} />
              </button>
              
              <div className="flex-1 relative">
                <input
                  type="text"
                  value={currentMessage}
                  onChange={(e) => setCurrentMessage(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                  className="w-full bg-pink-50 rounded-full px-4 py-3 pr-12 focus:ring-2 ring-pink-300 focus:outline-none"
                  placeholder="Share your thoughts, questions, or concerns..."
                  disabled={isLoading}
                />
                <button 
                  className="absolute right-3 top-1/2 transform -translate-y-1/2 bg-pink-500 text-white p-1 rounded-full"
                  onClick={handleSendMessage}
                  disabled={isLoading}
                >
                  <Send size={16} className="transform rotate-[-45deg]" />
                </button>
              </div>
              
              <button 
                className="ml-2 bg-gradient-to-r from-pink-500 to-rose-500 text-white p-3 rounded-full shadow-md"
                onClick={() => setShowEmergencyChecklist(true)}
              >
                <AlertCircle size={20} />
              </button>
            </div>
            
            <div className="flex justify-center mt-2 space-x-1">
              <button className="text-xs bg-pink-100 text-pink-700 px-3 py-1 rounded-full">
                Ask about symptoms
              </button>
              <button className="text-xs bg-pink-100 text-pink-700 px-3 py-1 rounded-full">
                Feeling anxious
              </button>
              <button className="text-xs bg-pink-100 text-pink-700 px-3 py-1 rounded-full">
                Baby development
              </button>
            </div>
          </div>
        </div>
      )}
      
      {/* Resource Panel */}
      {showResourcePanel && <ResourcePanel />}
      
      {/* Emergency Checklist */}
      {showEmergencyChecklist && <EmergencyChecklist />}
      
      {/* Always-available support indicator */}
      <div className="fixed bottom-4 right-4 bg-white p-3 rounded-full shadow-lg z-10">
        <div className="bg-gradient-to-r from-pink-500 to-rose-500 text-white rounded-full p-2 animate-pulse">
          <Heart size={20} />
        </div>
      </div>
    </div>
  );
};

// ==================== APP EXPORT ====================
export default function App() {
  return (
    <div className="font-sans max-w-md mx-auto h-screen overflow-hidden">
      <FridayAI />
    </div>
  );
}
